import base64
from fontTools.ttLib import TTFont

font_face = ""

print(len(font_face))

b = base64.b64decode(font_face)
with open('font2.ttf', 'wb') as f:
    f.write(b)

font = TTFont('font2.ttf')
font.saveXML('02.xml')